<!-- Written by www.freecontactform.com --><div id="contactform"> Enable Javascript </div><div id="modalmessage"> </div><script>	document.onload = function () { createForm(); };		function submitContact() {		var captcha = document.getElementsByName("captcha")[0].value;		var name = document.getElementsByName("name")[0].value;		var email = document.getElementsByName("email")[0].value;		var phone = document.getElementsByName("phone")[0].value;		var company = document.getElementsByName("company")[0].value;		var message = document.getElementsByName("message")[0].value;				var xmlHttpReq;		// xmlhttp = window.XMLHttpRequest ? new XMLHttpRequest() : new ActiveXObject("Microsoft.XMLHTTP");		if(window.XMLHttpRequest)			xmlHttpReq = new XMLHttpRequest();		else // for older versions of IE						xmlHttpReq = new ActiveXObject("Microsoft.XMLHTTP");					// attempt to open the file		xmlHttpReq.open("POST", "http://78.24.208.245/wp-content/plugins/c8contactmodal/mailresponse.php", true);		// Prepare request header and fields then send		xmlHttpReq.setRequestHeader("Content-type","application/x-www-form-urlencoded");				xmlHttpReq.send("captcha="+captcha+"&name="+name+"&email="+email+"&phone="+phone+"&company="+company+"&message="+message);		// wait until the ready		xmlHttpReq.onreadystatechange = function () {			if(xmlHttpReq.readyState == 4 && xmlHttpReq.status == 200) {				var response = xmlHttpReq.responseText;							if(response.indexOf("success") > 0) {					document.getElementById("contactform").innerHTML = response;						document.getElementById("modalmessage").innerHTML = "";					return;				}				else {					document.getElementById("modalmessage").innerHTML = response;							boxHighlight(response);				}			}			else {				// document.getElementById("modalmessage").innerHTML = "A failure occured - the request never finished, response was not ready or page not found";			}		}	}		function createForm() {		document.getElementById("contactform").innerHTML = 'Please fill out the form:<form name="contactform" method="post" action=""><table width="450px"><tr> <td valign="top">  <label for="name">Name *</label> </td> <td valign="top">  <input  type="text" name="name" maxlength="50" size="30"> </td></tr><tr> <td valign="top">  <label for="email">Email *</label> </td> <td valign="top">  <input  type="text" name="email" maxlength="80" size="30"> </td></tr><tr> <td valign="top">  <label for="phone">Phone</label> </td> <td valign="top">  <input  type="text" name="phone" maxlength="30" size="30"> </td></tr><tr> <td valign="top">  <label for="company">Company</label> </td> <td valign="top">  <input  type="text" name="company" maxlength="30" size="30"> </td></tr><tr> <td valign="top">  <label for="message">Message *</label> </td> <td valign="top">  <textarea  name="message" maxlength="1000" cols="25" rows="6"></textarea> </td></tr><tr> <td valign="top" align="right" id="captcha_image">  <img src="http://78.24.208.245/wp-content/plugins/c8contactmodal/mathcaptcha_image.php" width="80" height="50"/> </td> <td valign="middle">  <input type="text"  name="captcha" maxlength="4" size="30"></input> </td></tr></table></form><button id="formsubmit" onclick="submitContact()">Submit</button>';	}		function boxHighlight (rstring) {		document.getElementsByName("captcha")[0].style.border = 		document.getElementsByName("message")[0].style.border = 		document.getElementsByName("email")[0].style.border = 		document.getElementsByName("name")[0].style.border = "none";			if(rstring.toLowerCase().indexOf("equation") > 0 || rstring.toLowerCase().indexOf("answer") > 0) {			document.getElementsByName("captcha")[0].style.border = "solid 2px red";			document.getElementsByName("captcha")[0].focus();		}		if(rstring.toLowerCase().indexOf("message") > 0) {			document.getElementsByName("message")[0].style.border = "solid 2px red";			document.getElementsByName("message")[0].focus();		}		if(rstring.toLowerCase().indexOf("email") > 0) {			document.getElementsByName("email")[0].style.border = "solid 2px red";			document.getElementsByName("email")[0].focus();		}		if(rstring.toLowerCase().indexOf("name") > 0) {			document.getElementsByName("name")[0].style.border = "solid 2px red";			document.getElementsByName("name")[0].focus();		}}</script>